import React, { useEffect, useState } from "react";
import axios from "axios"
import PostForm from "./components/PostForm"
import "./App.css";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      users: null
    }
  }
  componentDidMount() {
    fetch('https://mockrestapi.herokuapp.com/api/employee?pageNo=1&limit=5').then((resp) => {
      resp.json().then((result) => {
        this.setState({ users: result.data });
      });
    });

  }

  render() {
    const postDelete = (id, e) => {
        e.preventDefault();
        axios.delete(`https://mockrestapi.herokuapp.com/api/employee/${id}`)
          .then(res => console.log('Deleted..', res)).catch(err => console.log(err))
      }


  return (
  <div clssname="app-container">
    <table>
      <thead>
        <tr>
          <th>name</th>
          <th>email</th>
          <th>phone</th>
          <th>address</th>
          <th>country</th>
          <th>age</th>
          <th>Delete op</th>
        </tr>
      </thead>
     <tbody>
      {
     this.state.users ?
              this.state.users.map((item, i) =>
                <tr>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.phone}</td>
                  <td>{item.address}</td>
                  <td>{item.country}</td>
                  <td>{item.age}</td>
                  <th><button onClick={(e) => postDelete(item._id, e)}>Delete</button></th>
                </tr>
              )
              :
              null
            }
     </tbody>
      </table>
      <h2>Add new field</h2>
      <form>
      <input type ="text" 
        name="_id"
        required="required"
        placeholder="enter the _id"/>

         <input type ="text" 
        name="name"
        required="required"
        placeholder="enter the name"
        />
         <input type ="text" 
        email="emal"
        required="required"
        placeholder="enter the email"
        />
         <input type ="text" 
        phone="phone"
        required="required"
        placeholder="enter the phone"
        />
         <input type ="text" 
        address="address"
        required="required"
        placeholder="enter the address"
        />
         <input type ="text" 
        country="country"
        required="required"
        placeholder="enter the id"
        />
         <input type ="text" 
        age="age"
        required="required"
        placeholder="enter the age"
        />
      </form>

  </div>
  );
};
}
export  default App;
