import React,{useState} from "react";
import Axios from "axios";

function PostForm(){
    const url="https://mockrestapi.herokuapp.com/api/employee"
    const[data,setData]=useState({
        _id:"",
        name:"",
        phone:"",
        email:"",
        country:"",
        age:"",
        address:""


    })
    function add(e){
        e.preventDefault();
        Axios.post(url,{
            id:parseInt(data._id),
            
            name:data.name,
            email:data.email,
            phone:data.phone,
            address:data.address,
            country:data.country,
            age:data.age,
            

        })
        .then(res=>{
            console.log(res.data)
        })

    }
    

    function handle(e){
        const newdata={...data}
        newdata[e.target.id]=e.target.value
        setData(newdata)
        console.log(newdata)

    }
    return(
        <div>
            <h2>add new information</h2>
            <form onadd={(e)=> add(e)}>
            
                <input onChange={(e)=>handle(e)} id="name" value={data.name} placeholder="name" type="text"></input>
                <input onChange={(e)=>handle(e)} id="_id" value={data._id} placeholder="email" type="email"></input>
                <input onChange={(e)=>handle(e)} id="phone" value={data.phone} placeholder="phone" type="tel"></input>
                <input onChange={(e)=>handle(e)} id="email" value={data.email}placeholder="address" type="text"></input>
                <input onChange={(e)=>handle(e)} id="country" value={data.country} placeholder="country" type="text"></input>
                <input onChange={(e)=>handle(e)} id="age" value={data.age} placeholder="age" type="age"></input>
                
                <button>Add</button>
            
            </form>
        </div>
        
    );
};
export default PostForm;
