import React,{userState} from"react";
import Axios from"axios";
function PostForm(){
    const url="mockrestapi.herokuapp.com/api/employee?pageNo=1&limit=5"
    const[data,setData]=userState({
        _id:"",
        name:"",
        phone:"",
        email:"",
        country:"",
        age:"",
        address:""


    })
    function add(e){
        e.preventDefault();
        Axios.post(url,{
            _id:data._id,
            name:data.name,
            phone:data.phone,
            email:data.email,
            country:data.country,
            age:data.age,
            address:data.address

        })
        .then

    }
    

    function handle(e){
        const newdata={...data}
        newdata[e.target.id]=e.target.value
        setData(newdata)
        console.log(newdata)

    }
    return(
        <div>
            <form onadd={(e)=> add(e)}>
                <input onChange={(e)=>handle(e)} id="_id" value={data._id} placeholder="_id" type="string"></input>
                <input onChange={(e)=>handle(e)} id="name" value={data.name} placeholder="name" type="text"></input>
                <input onChange={(e)=>handle(e)} id="phone" value={data.phone} placeholder="phone" type="tel"></input>
                <input onChange={(e)=>handle(e)} id="email" value={data.email}placeholder="email" type="email"></input>
                <input onChange={(e)=>handle(e)} id="country" value={data.country} placeholder="country" type="text"></input>
                <input onChange={(e)=>handle(e)} id="age" value={data.age} placeholder="age" type="age"></input>
                <input onChange={(e)=>handle(e)} id="address" value={data.address} placeholder="text" type="string"></input>
                <button>add</button>
            
            </form>
        </div>
        
    );
}
export default PostForm;
